INSERT INTO `laptopfusionwp123_aioseo_notifications` (`id`, `slug`, `title`, `content`, `type`, `level`, `notification_id`, `notification_name`, `start`, `end`, `button1_label`, `button1_action`, `button2_label`, `button2_action`, `dismissed`, `new`, `created`, `updated`) VALUES
(1, '621fa1b8be2b9', 'Search Engines Blocked', 'Warning: AIOSEO has detected that you are blocking access to search engines. You can change this in Settings > Reading if this was unintended.', 'error', '[\"all\"]', NULL, 'blog-visibility', '2022-03-02 16:56:24', NULL, 'Fix Now', 'http://localhost/laptopfusionwp/wp-admin/options-reading.php', 'Remind Me Later', 'http://action#notification/blog-visibility-reminder', 0, 0, '2022-03-02 16:56:24', '2022-03-02 16:56:24'),
(2, '621fa1faed701', 'ðŸ“¢ Announcing 4.1.8: Improved Site Redirects and Modified Date for Posts ðŸ“¢', 'Redirects have never been easier! Our 4.1.8 update comes with a powerful feature that enables you to implement a redirect from right within a post.\r\n<br><br>\r\nWeâ€™ve also added a \"limit modified date\" feature that allows you to make minor edits to posts/pages and keep the date the same.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.8 today and enjoy these 2 powerful features and more.', 'success', '[\"4-x\",\"lite\"]', 241, NULL, '2022-03-01 16:19:06', NULL, 'Learn More', 'https://aioseo.com/announcing-improved-site-redirects-and-modified-date-for-posts/?utm_source=WordPress&utm_campaign=introducing-on-page-redirects-and-limit-modified-date-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=introducing-on-page-redirects-and-limit-modified-date-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-03-02 16:57:30', '2022-03-02 16:57:42'),
(3, '621fa1fb2c9f9', 'ðŸŽ‰ Introducing IndexNow and Page Builder Integrations', 'Check out the fantastic new features in AIOSEO 4.1.7! First, we have IndexNow, a feature that gets your new or updated content indexed faster on Bing and Yandex (with more search engines to follow).\r\n<br><br>\r\nWeâ€™ve also integrated AIOSEO with 3 major page builders, namely Elementor, Divi and SeedProd. Now you can work on your SEO right from within your page builder.\r\n<br><br>\r\nGet AIOSEO Pro today and up your SEO game without breaking a sweat!', 'success', '[\"4-x\",\"lite\"]', 223, NULL, '2022-03-01 16:19:06', NULL, 'Learn More', 'https://aioseo.com/introducing-indexnow-and-page-builder-integrations-in-aioseo-4-1-7/?utm_source=WordPress&utm_campaign=index-now-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=index-now-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-03-02 16:57:31', '2022-03-02 16:57:42'),
(4, '627dd21c98d23', 'ðŸ“¢ Introducing 2 New Features! â€” Dashboard Widgets And Shorter URLs ðŸ“¢', 'Weâ€™ve added 2 new dashboard widgets. One to help you set up your website for better SEO and the other to show you an overview of how optimized your posts are.\r\n<br><br>\r\nAnother feature youâ€™ll love is the Strip Category Base Prefix feature that enables you to create shorter URLs by removing the category base.\r\n<br><br>\r\nUpgrade to AIOSEO 4.2.0 Pro today and enjoy these powerful features on your website!!', 'success', '[\"4-x\",\"lite\"]', 308, NULL, '2022-05-31 21:15:05', NULL, 'Learn More', 'https://aioseo.com/new-dashboard-widgets-and-strip-category-base-feature/?utm_source=WordPress&utm_campaign=dashboard-widgets-strip-category-base-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=dashboard-widgets-strip-category-base-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-05-13 03:35:56', '2022-06-20 00:47:33'),
(5, '627dd21d66ba6', 'Microsoft Clarity Integration and a New REST API Addon ðŸŽ‰ðŸŽ‰', 'Now you can see how your website visitors interact with your web pages with heatmaps and session recordings, thanks to Microsoft Clarity.\r\n<br><br>\r\nWeâ€™ve also added a REST API addon to make SEO easier for your headless website.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.9 today and enjoy these 2 powerful features on your website!', 'success', '[\"4-x\",\"lite\"]', 263, NULL, '2022-05-03 15:26:42', NULL, 'Learn More', 'https://aioseo.com/introducing-the-new-microsoft-clarity-integration-and-rest-api-addon/?utm_source=WordPress&utm_campaign=microsoft-clarity-rest-api-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=microsoft-clarity-rest-api-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-05-13 03:35:57', '2022-05-13 03:35:57'),
(6, '62a654fdb9048', 'ðŸ“£ Introducing Crawl Cleanup and Import URLs to Your Sitemap!', 'AIOSEO 4.2.1 now allows you to manage Search Engine crawl quota by removing query args (URL parameters) from selected URLs and disabling unnecessary RSS feeds.\r\n<br><br>\r\nYou can also import bulk URLs from a CSV file to add more pages to your sitemap.\r\n<br><br>\r\nUpdate to AIOSEO 4.2.1 to take advantage of these powerful SEO features today!', 'success', '[\"4-x\",\"lite\"]', 327, NULL, '2022-07-19 14:22:11', NULL, 'Learn More', 'https://aioseo.com/introducing-crawl-cleanup-and-importing-urls-from-a-csv-file-to-your-sitemap/?utm_source=WordPress&utm_campaign=crawl-cleanup-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/pricing/?utm_source=WordPress&utm_campaign=crawl-cleanup-v4-lite&utm_medium=plugin-notification&utm_content=Get%20AIOSEO%20Pro', 0, 0, '2022-06-12 21:05:01', '2022-07-21 18:01:29'),
(7, '62b38c385deae', 'AIOSEO 4.2.2 is HERE with some great new features! ðŸŽ‰', 'Connect your WordPress user profile directly to your social media accounts for improved SEO.\r\n<br><br>\r\nOur new Translations API enables third-party translation apps to hook into AIOSEOâ€™s data for multilingual SEO.\r\n<br><br>\r\nUpdate to AIOSEO 4.2.2 now and enjoy more SEO opportunities!', 'success', '[\"4-x\",\"lite\"]', 338, NULL, '2022-07-19 14:22:11', NULL, 'Learn More', 'https://aioseo.com/announcing-our-new-user-social-profile-tab-and-translation-api-in-aioseo-4-2-2/?utm_source=WordPress&utm_campaign=social-profiles-translations-api-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=social-profiles-translations-api-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-06-22 21:40:08', '2022-07-21 18:01:28'),
(8, '62d6a46ba8bf3', 'ðŸ“£ AIOSEO 4.2.3: Introducing FAQ and Table of Contents blocks + URL Slug Monitor.', 'You can now add <strong>FAQ and Table of Contents blocks</strong> to increase your visibility on SERPs and improve user experience.\r\n<br><br>\r\nAnd our <strong>URL Slug Monitor</strong> is a powerful addition that checks for changes in your URLs and prompts you to add redirects which helps <strong>prevents 404 errors</strong> on your site.\r\n<br><br>\r\nUpdate to 4.2.3 today and check out these powerful new features!', 'success', '[\"4-x\",\"lite\"]', 414, NULL, '2022-07-19 14:22:11', NULL, 'Learn More', 'https://aioseo.com/introducing-aioseo-4-2-3-faq-and-table-of-contents-blocks-all-new-url-slug-monitor/?utm_source=WordPress&utm_campaign=introducing-faq-toc-blocks-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=introducing-faq-toc-blocks-vv-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-07-19 12:32:43', '2022-07-21 18:01:28');
